mapper.select:
SELECT a.id, a.name, a.url, a.method, a.type, a.parent_id, a.status, a.position, a.menu_category_id FROM sys_privilege a WHERE 1=1
and a.status = #{status}
order by a.position asc

SELECT a.id, a.name, a.url, a.method, a.type, a.parent_id, a.status, a.menu_category_id, a.position FROM sys_privilege a WHERE a.type=1
and a.status = #{status}
order by a.position asc

SELECT a.id, a.name, a.url, a.method, a.type, a.parent_id, a.status, a.menu_category_id, a.position, b.name as menu_category_name FROM sys_privilege a left join sys_privilege b on a.menu_category_id=b.id WHERE a.type=1
and a.status = #{status}
order by a.position asc

SELECT a.id, a.name, a.url, a.method, a.type, a.parent_id, a.status, a.menu_category_id, a.position
FROM sys_privilege a
WHERE a.id = #{id}

mapper.delete:
delete from sys_privilege
where id in

	#{id}
